INSERT INTO heroes (name, description, image) VALUES ('Hulk','Hulk es un personaje ficticio, que aparece en los cómics estadounidenses publicados por la editorial Marvel Comics. El personaje fue creado por los escritores Stan Lee y Jack Kirby siendo su primera aparición en The Incredible Hulk #1 publicado en mayo de 1962​','https://i.kinja-img.com/gawker-media/image/upload/s--Q9wAeXxC--/c_scale,f_auto,fl_progressive,q_80,w_800/daobdnk4qaiix29vrnfn.jpg');
INSERT INTO heroes (name, description, image) VALUES ('Flash', 'Flash es el nombre de varios superhéroes ficticios que aparecen en los cómics estadounidenses publicados por DC Comics. Creado por el escritor Gardner Fox y el artista Harry Lampert, el "Flash" original apareció por primera vez en Flash Comics #1 )','https://heroichollywood.com/wp-content/uploads/2018/10/Flash_DC_Comics_CW.jpg');
INSERT INTO heroes (name, description, image) VALUES ('IronMan', 'Iron Man es un superhéroe ficticio que aparece en los cómics estadounidenses publicados por Marvel Comics. El personaje fue co-creado por el escritor y editor Stan Lee, desarrollado por el guionista Larry Lieber y diseñado por los artistas Don Heck y Jack Kirby.','http://static.t13.cl/images/sizes/1200x675/1564174793-blob75yu.jpg');
INSERT INTO heroes (name, description, image) VALUES ('BatMan', 'Batman es un personaje creado por los estadounidenses Bob Kane y Bill Finger, y propiedad de DC Comics. Apareció por primera vez en la historia titulada «El caso del sindicato químico» de la revista Detective Comics N.º 27, lanzada por la editorial National Publications el 30 de marzo de 1939','http://img.chilango.com/2015/06/batman.jpg');
